import logo from './logo.svg';
import './App.css';
import React, { useState } from "react";

function App() {
  const [quantidade, setQuantidade] = useState('');
  const [produto, setProduto] = useState('');
  const [preco, setPreco] = useState('');
  const [listaProdutos, setListaProdutos] = useState([]);

  const adicionarProduto = (e) => {
    e.preventDefault();
    if (quantidade && produto && preco) {
      const novoProduto = { quantidade, produto, preco };
      setListaProdutos([...listaProdutos, novoProduto]);
      setQuantidade('');
      setProduto('');
      setPreco('');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center">
      <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
        <h1 className="text-2xl font-bold mb-6 text-gray-800 text-center">PDV - Compra de Mercadorias</h1>

        <form onSubmit={adicionarProduto} className="space-y-4">
          {/* Campo de Quantidade */}
          <div>
            <label htmlFor="quantidade" className="block text-gray-700">Quantidade (kg/unidade):</label>
            <input
              type="number"
              id="quantidade"
              value={quantidade}
              onChange={(e) => setQuantidade(e.target.value)}
              className="mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm focus:ring focus:ring-blue-500 focus:outline-none"
              required
            />
          </div>

          {/* Campo de Pesquisa de Produto */}
          <div>
            <label htmlFor="produto" className="block text-gray-700">Produto:</label>
            <input
              type="text"
              id="produto"
              value={produto}
              onChange={(e) => setProduto(e.target.value)}
              className="mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm focus:ring focus:ring-blue-500 focus:outline-none"
              placeholder="Pesquisar produto"
              required
            />
          </div>

          {/* Campo de Preço */}
          <div>
            <label htmlFor="preco" className="block text-gray-700">Preço (R$):</label>
            <input
              type="number"
              step="0.01"
              id="preco"
              value={preco}
              onChange={(e) => setPreco(e.target.value)}
              className="mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm focus:ring focus:ring-blue-500 focus:outline-none"
              required
            />
          </div>

          {/* Botão de Adicionar Produto */}
          <button
            type="submit"
            className="w-full bg-blue-600 text-white font-bold py-2 px-4 rounded-md hover:bg-blue-700 transition"
          >
            Adicionar Produto
          </button>
        </form>

        {/* Lista de Produtos */}
        <div className="mt-6">
          <h2 className="text-lg font-semibold text-gray-800">Produtos Adicionados:</h2>
          <ul className="list-disc list-inside mt-2">
            {listaProdutos.map((item, index) => (
              <li key={index} className="text-gray-700">
                {item.quantidade} kg/un de {item.produto} - R${item.preco}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default App;
