import React, { useState, useEffect, useRef } from 'react';

function Compra() {
  const [quantidade, setQuantidade] = useState('');
  const [produto, setProduto] = useState('');
  const [preco, setPreco] = useState('');
  const [listaProdutos, setListaProdutos] = useState([]);
  const [produtosFiltrados, setProdutosFiltrados] = useState([]);
  const [produtoSelecionadoIndex, setProdutoSelecionadoIndex] = useState(-1); // Estado para controlar o item selecionado na lista
  const quantidadeRef = useRef(null);
  const produtoRef = useRef(null);
  const precoRef = useRef(null);

  useEffect(() => {
    // Coloca o foco automaticamente no campo de quantidade ao carregar a página
    quantidadeRef.current.focus();
  }, []);

  // Função para buscar produtos na API com base no que o usuário digita
  const buscarProduto = async (query) => {
    try {
      const response = await fetch(`http://back.elitevenda.com.br:3001/api/produtos?q=${query}`);
      const data = await response.json();
      setProdutosFiltrados(data);
      setProdutoSelecionadoIndex(-1); // Reseta o índice ao buscar novos produtos
    } catch (error) {
      console.error('Erro ao buscar produtos:', error);
    }
  };

  const handleQuantidadeEnter = (e) => {
    if (e.key === 'Enter') {
      produtoRef.current.focus(); // Foco vai para o campo de produto
    }
  };

  const handleProdutoChange = (e) => {
    setProduto(e.target.value);
    buscarProduto(e.target.value); // Busca os produtos em tempo real
  };

  const handleProdutoKeyDown = (e) => {
    if (e.key === 'ArrowDown') {
      // Navegar para baixo na lista de produtos
      setProdutoSelecionadoIndex((prevIndex) =>
        prevIndex < produtosFiltrados.length - 1 ? prevIndex + 1 : prevIndex
      );
    } else if (e.key === 'ArrowUp') {
      // Navegar para cima na lista de produtos
      setProdutoSelecionadoIndex((prevIndex) =>
        prevIndex > 0 ? prevIndex - 1 : prevIndex
      );
    } else if (e.key === 'Enter') {
      if (produtoSelecionadoIndex >= 0) {
        // Selecionar o produto atual ao pressionar Enter
        const produtoSelecionado = produtosFiltrados[produtoSelecionadoIndex];
        selecionarProduto(produtoSelecionado);
      }
    }
  };

  const selecionarProduto = (produto) => {
    setProduto(produto.nome);
    setPreco(produto.preco);
    setProdutosFiltrados([]); // Limpa a lista após a seleção
    precoRef.current.focus(); // Foco vai para o campo de preço
  };

  const handlePrecoEnter = (e) => {
    if (e.key === 'Enter') {
      adicionarProduto();
    }
  };

  const adicionarProduto = () => {
    if (quantidade && produto && preco) {
      const novoProduto = { quantidade, produto, preco };
      setListaProdutos([...listaProdutos, novoProduto]);
      setQuantidade('');
      setProduto('');
      setPreco('');
      quantidadeRef.current.focus(); // Volta para o campo de quantidade
    }
  };

  const finalizarCompra = () => {
    // Atualizar o estoque e limpar o carrinho
    alert('Compra finalizada e estoque atualizado');
    gerarRecibo(); // Chama a função para imprimir o recibo
    setListaProdutos([]);
  };

  const gerarRecibo = () => {
    const reciboWindow = window.open('', '', 'width=400,height=600');
    reciboWindow.document.write('<html><head><title>Recibo</title></head><body>');
    reciboWindow.document.write('<h2>Recibo de Compra</h2>');
    reciboWindow.document.write('<table><thead><tr><th>Produto</th><th>Qtd</th><th>Preço</th></tr></thead><tbody>');
    listaProdutos.forEach(produto => {
      reciboWindow.document.write(`<tr><td>${produto.produto}</td><td>${produto.quantidade}</td><td>${produto.preco}</td></tr>`);
    });
    reciboWindow.document.write('</tbody></table>');
    reciboWindow.document.write('<p>Obrigado pela compra!</p>');
    reciboWindow.document.write('</body></html>');
    reciboWindow.document.close();
    reciboWindow.print(); // Abre a janela de impressão
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center">
      <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
        <h1 className="text-2xl font-bold mb-6 text-gray-800 text-center">PDV - Compra de Mercadorias</h1>

        <div className="space-y-4">
          {/* Campo de Quantidade */}
          <div>
            <label className="block text-gray-700">Quantidade (kg/unidade):</label>
            <input
              type="number"
              value={quantidade}
              onChange={(e) => setQuantidade(e.target.value)}
              onKeyDown={handleQuantidadeEnter}
              ref={quantidadeRef}
              className="mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm focus:ring focus:ring-blue-500 focus:outline-none"
              required
            />
          </div>

          {/* Campo de Produto */}
          <div>
            <label className="block text-gray-700">Produto:</label>
            <input
              type="text"
              value={produto}
              onChange={handleProdutoChange}
              onKeyDown={handleProdutoKeyDown}
              ref={produtoRef}
              className="mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm focus:ring focus:ring-blue-500 focus:outline-none"
              placeholder="Pesquisar produto"
              required
            />
            {/* Exibe os produtos filtrados */}
            {produtosFiltrados.length > 0 && (
              <ul className="mt-2 bg-white border border-gray-300 rounded-md shadow-sm">
                {produtosFiltrados.map((p, index) => (
                  <li
                    key={p.id}
                    className={`cursor-pointer p-2 hover:bg-gray-100 ${index === produtoSelecionadoIndex ? 'bg-gray-200' : ''}`}
                    onClick={() => selecionarProduto(p)}
                  >
                    {p.nome} - R${p.preco}
                  </li>
                ))}
              </ul>
            )}
          </div>

          {/* Campo de Preço */}
          <div>
            <label className="block text-gray-700">Preço (R$):</label>
            <input
              type="number"
              step="0.01"
              value={preco}
              onChange={(e) => setPreco(e.target.value)}
              onKeyDown={handlePrecoEnter}
              ref={precoRef}
              className="mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm focus:ring focus:ring-blue-500 focus:outline-none"
              required
            />
          </div>

          {/* Botão de Finalizar Compra */}
          <button
            className="w-full bg-green-600 text-white font-bold py-2 px-4 rounded-md hover:bg-green-700 transition"
            onClick={finalizarCompra}
          >
            Finalizar Compra
          </button>
        </div>

        {/* Lista de Produtos */}
        <div className="mt-6">
          <h2 className="text-lg font-semibold text-gray-800">Carrinho:</h2>
          <ul className="list-disc list-inside mt-2">
            {listaProdutos.map((item, index) => (
              <li key={index} className="text-gray-700">
                {item.quantidade} kg/un de {item.produto} - R${item.preco}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Compra;
